<?phpfunction activateActivityType($entity){	$currentModule = 'XActivityType';	$report_arr = explode('x',$entity->id);	$record = $report_arr[1];    $focus = CRMEntity::getInstance($currentModule);    $focus->retrieve_entity_info($record, $currentModule);    $focus->id  = $record;		$focus->column_fields['z_act_active'] = '1';	$focus->mode = "edit";		$focus->save($currentModule);}function deactivateActivityType($entity){	$currentModule = 'XActivityType';	$report_arr = explode('x',$entity->id);	$record = $report_arr[1];    $focus = CRMEntity::getInstance($currentModule);    $focus->retrieve_entity_info($record, $currentModule);    $focus->id  = $record;		$focus->column_fields['z_act_active'] = '';	$focus->mode = "edit";		$focus->save($currentModule);}?>