<?phpfunction activateBusinessUnit($entity){	$currentModule = 'XBusinessUnit';	$report_arr = explode('x',$entity->id);	$record = $report_arr[1];    $focus = CRMEntity::getInstance($currentModule);    $focus->retrieve_entity_info($record, $currentModule);    $focus->id  = $record;		$focus->column_fields['z_bu_isactive'] = '1';	$focus->mode = "edit";		$focus->save($currentModule);}function deactivateBusinessUnit($entity){	$currentModule = 'XBusinessUnit';	$report_arr = explode('x',$entity->id);	$record = $report_arr[1];    $focus = CRMEntity::getInstance($currentModule);    $focus->retrieve_entity_info($record, $currentModule);    $focus->id  = $record;		$focus->column_fields['z_bu_isactive'] = '';	$focus->mode = "edit";		$focus->save($currentModule);}?>