<?phpfunction activateCompetitor($entity){	$currentModule = 'XCompetitor';	$report_arr = explode('x',$entity->id);	$record = $report_arr[1];    $focus = CRMEntity::getInstance($currentModule);    $focus->retrieve_entity_info($record, $currentModule);    $focus->id  = $record;		$focus->column_fields['z_cm_isactv'] = '1';	$focus->mode = "edit";		$focus->save($currentModule);}function deactivateCompetitor($entity){	$currentModule = 'XCompetitor';	$report_arr = explode('x',$entity->id);	$record = $report_arr[1];    $focus = CRMEntity::getInstance($currentModule);    $focus->retrieve_entity_info($record, $currentModule);    $focus->id  = $record;		$focus->column_fields['z_cm_isactv'] = '';	$focus->mode = "edit";		$focus->save($currentModule);}?>