<?phpfunction activateMarketingMat($entity){	$currentModule = 'XMarketingMat';	$report_arr = explode('x',$entity->id);	$record = $report_arr[1];    $focus = CRMEntity::getInstance($currentModule);    $focus->retrieve_entity_info($record, $currentModule);    $focus->id  = $record;		$focus->column_fields['z_mm_isactv'] = '1';	$focus->mode = "edit";		$focus->save($currentModule);}function deactivateMarketingMat($entity){	$currentModule = 'XMarketingMat';	$report_arr = explode('x',$entity->id);	$record = $report_arr[1];    $focus = CRMEntity::getInstance($currentModule);    $focus->retrieve_entity_info($record, $currentModule);    $focus->id  = $record;		$focus->column_fields['z_mm_isactv'] = '';	$focus->mode = "edit";		$focus->save($currentModule);}?>