<?phpfunction approveToRegionalWorkplanEntry($entity){	$currentModule = 'XWorkplanEntry';	$report_arr = explode('x',$entity->id);	$record = $report_arr[1];    $focus = CRMEntity::getInstance($currentModule);    $focus->retrieve_entity_info($record, $currentModule);    $focus->id  = $record;		$focus->column_fields['z_wpe_status'] = 'Approved by Regional or Area Manager';	$focus->mode = "edit";		$focus->save($currentModule);}function approveToNSMWorkplanEntry($entity){	$currentModule = 'XWorkplanEntry';	$report_arr = explode('x',$entity->id);	$record = $report_arr[1];    $focus = CRMEntity::getInstance($currentModule);    $focus->retrieve_entity_info($record, $currentModule);    $focus->id  = $record;		$focus->column_fields['z_wpe_status'] = 'Approved by NSM';	$focus->mode = "edit";		$focus->save($currentModule);}?>